<!DOCTYPE html PUBLIC "-//W3C//DTD XHTML 1.0 Transitional//EN" "http://www.w3.org/TR/xhtml1/DTD/xhtml1-transitional.dtd">
<html xmlns="http://www.w3.org/1999/xhtml">
<head>
<meta http-equiv="Content-Type" content="text/html; charset=utf-8" />
<title>跳转提示</title>
<meta name="viewport" content="width=device-width, initial-scale=1, maximum-scale=1, user-scalable=no">
<meta name="renderer" content="webkit">
<meta http-equiv="Cache-Control" content="no-siteapp" />
<style type="text/css">
*{box-sizing:border-box;margin:0;padding:0;font-family:Lantinghei SC,Open Sans,Arial,Hiragino Sans GB,Microsoft YaHei,"微软雅黑",STHeiti,WenQuanYi Micro Hei,SimSun,sans-serif;-webkit-font-smoothing:antialiased}html{font-size:10px;-webkit-tap-highlight-color:rgba(0,0,0,0)}html{font-family:sans-serif;-webkit-text-size-adjust:100%;-ms-text-size-adjust:100%}body{font-family:"Helvetica Neue",Helvetica,Arial,sans-serif;font-size:14px;line-height:1.42857143;color:#333;background-color:#fff}body{margin:0}p{margin:0 0 10px}.system-message{padding:24px 48px;margin:auto;box-shadow:0px 0px 10px rgba(0,0,0,.2)!important;top:50%;width:500px;border-radius:2px;-moz-border-radius:10px}.system-message h1{font-size:100px;font-weight:normal;line-height:120px;margin-bottom:12px}.system-message .jump{padding-top:10px;color:#999;text-align:center}.system-message .jump a{color:#333}.system-message .success,.system-message .error{line-height:1.8em;color:#000;font-size:18px;font-weight:bold;text-align:center}.system-message .detail{font-size:12px;line-height:20px;margin-top:12px;display:none}.status-ico{margin:20px auto;width:73px;height:76px;display:block;background-repeat:no-repeat;background-position:center;}.status-ico-ok{background-image:url('data:image/png;base64,');}.status-ico-error{background-image:url('data:image/png;base64,');}.status-ico-operation{background-image:url('data:image/png;base64,');}@media (max-width:768px){body{padding:20px 0;}}@media (max-width:480px){.system-message h1{font-size:30px;}}
</style>
</head>
<body>
<div class="system-message"> @switch($code)
  @case(200)
  <div class="status-ico status-ico-ok"> </div>
  <p class="success"> {{ strip_tags($message) }} </p>
  @break
  @case(404)
  <div class="status-ico status-ico-error"> </div>
  <p class="error"> {{ strip_tags($message) }} </p>
  @break 
  @default
  <div class="status-ico status-ico-operation"> </div>
  <p class="error"> {{ strip_tags($message) }} </p>
  @endswitch
  <p class="jump"> 页面自动 <a id="href" href="{{$url}}" target="{{$target}}"> 跳转 </a> 等待时间： <b id="wait"> {{$wait}} </b> </p>
</div>
<script src="https://cdn.bootcdn.net/ajax/libs/jquery/1.12.3/jquery.min.js"></script> 
<script type="text/javascript">
(function() {
    var height2 = $('.system-message').height();
    var height1 = $(window).height();
    $('.system-message').css('margin-top', ((height1 - height2) / 3) - 30);
    var wait = document.getElementById('wait'),
        href = document.getElementById('href').href,
        target = document.getElementById('href').target;
    var interval = setInterval(function() {
            var time = --wait.innerHTML;
            if (time <= 0) {
                if ('_parent' == target) {
                    parent.location.href = href;
                } else {
                    location.href = href;
                }
                clearInterval(interval);
            };
        },
        1000);
})();   
</script>
</body>
</html>